INSERT INTO PRODUCT (PRODUCT_ID, PRODUCT_TITLE, CATEGORY, DESCRIPTION, PRICE, QUANTITY, IMAGE) VALUES (1, 'CHICKEN', 'WHITE MEAT','CHICKEN IS THE MOST COMMON TYPE OF POULTRY. CHICKEN MEAT AND EGGS HAVE BECOME COMMON IN MANY CUISINES DUE TO THE RELATIVE EASE AND LOW COST OF KEEPING CHICKENS COMPARED TO MAMMALS SUCH AS CATTLE OR HOGS', '2.46', '15' , 'chicken.jpg');

--TURKEY
INSERT INTO PRODUCT(PRODUCT_ID, PRODUCT_TITLE, CATEGORY, DESCRIPTION, PRICE, QUANTITY, IMAGE) VALUES('2','TURKEY','WHITE MEAT','TURKEY IS A LARGE POULTRY BIRD THAT IS SIMILAR TO CHICKEN IN THE WHITE MEAT CATEGORY. TURKEY MEAT SEEMS TO BE LITTLE DARKER THAN CHICKEN MEAT, BUT WITH FEWER CALORIES. MANY OF THE HEALTH BENEFITS OF TURKEY MEAT ARE SIMILAR TO CHICKEN. LEAN PROTEIN CAN BE FOUND IN TURKEY BREAST, DRUMSTICK, AND WINGS. TURKEY IS ALSO RELATIVELY CHEAP, SO IT IS A NUTRIENT-DENSE FOOD WITH A LOW COST.','4','35','turkey.jpg');

--DUCK
INSERT INTO PRODUCT(PRODUCT_ID, PRODUCT_TITLE, CATEGORY, DESCRIPTION, PRICE, QUANTITY, IMAGE) VALUES('3','DUCK','WHITE MEAT','DUCK IS ANOTHER BIRD IS ANOTHER WHITE MEAT CATEGORY,. DUCK MEAT IS DARKER THAN CHICKEN OR TURKEY, BUT IT IS STILL CONSIDERED AS WHITE MEAT. DUCKS ARE A HEALTHY MEAT TO CONSUME BECAUSE THEY ARE NUTRIENT-DENSE. ANTIOXIDANTS CAN ALSO BE FOUND IN COOKED DUCK. ONE STUDY FOUND THAT ROASTED DUCK MEAT HAS MORE ANTIOXIDANTS THAN UNCOOKED DUCK MEAT','2.64','10','duck.jpg');

--PORK
INSERT INTO PRODUCT(PRODUCT_ID, PRODUCT_TITLE, CATEGORY, DESCRIPTION, PRICE, QUANTITY, IMAGE) VALUES('4','PORK','RED MEAT','PORK IS CLASSIFIED AS RED MEAT DUE TO MYOGLOBIN LEVELS IN THE MEAT. EVEN THOUGH FRESH PORK IS LIGHTER IN COLOR THAN BEEF AND COOKS TO BECOME VERY LIGHT.','2.64','17','pork.jpg');

