INSERT INTO PRODUCT(PRODUCT_ID, PRODUCT_TITLE, CATEGORY, DESCRIPTION, PRICE, QUANTITY, IMAGE) VALUES('5','BEEF','RED MEAT','BEEF IS THE MOST COMMONLY CONSUMED RED MEAT. BEEF CHUNKS ARE GOOD IN STEWS, GROUND BEEF IS MADE INTO HAMBURGERS, AND DIFFERENT CUTS OF BEEF ARE USED FOR VARIOUS TYPES OF STEAK. BEEF CONTAINS MANY PROTEINS AND NUTRIENTS. IN FACT, IT IS ONE OF THE BEST DIETARY IRON SOURCES AVAILABLE.', '14.16','25','beef.jpg');

--LAMB
INSERT INTO PRODUCT(PRODUCT_ID, PRODUCT_TITLE, CATEGORY, DESCRIPTION, PRICE, QUANTITY, IMAGE) VALUES('6','LAMB','RED MEAT','LAMB IS THE MEAT OF YOUNG DOMESTIC SHEEP. IT IS A FORM OF RED MEAT, WHICH IS DEFINED AS MEAT FROM ANIMALS THAT HAS MORE IRON THAN POULTRY OR FISH. LAMB REFERS TO THE FLESH OF YOUNG SHEEP IN THEIR FIRST YEAR, WHEREAS MUTTON REFERS TO THE MEAT OF ADULT SHEEP.', '6.50','19','lamb.jpg');

