INSERT INTO PRODUCT(PRODUCT_ID, PRODUCT_TITLE, CATEGORY, DESCRIPTION, PRICE, QUANTITY, IMAGE) VALUES('7','POTATO','VEGETABLE','POTATOES ARE QUITE ROUND VEGETABLES WITH BROWN OR RED SKINS AND WHITE INSIDES.POTATOES ARE AN EXCELLENT SOURCE OF VITAMIN C, POTASSIUM, FIBRE, B VITAMINS COPPER, TRYPTOPHAN, MANGANESE AND EVEN LUTEIN, NOTES THE BOOK "HEALING FOODS" BY DK PUBLISHING. IT ALSO WORKS WONDERS FOR CURBING INFLAMMATION IN THE BODY AND BOOST IMMUNITY AND HEALTHY BLOOD CIRCULATION.', '0.97','100','potato.jpg');

--CAULIFLOWER
INSERT INTO PRODUCT(PRODUCT_ID, PRODUCT_TITLE, CATEGORY, DESCRIPTION, PRICE, QUANTITY, IMAGE) VALUES('8','CAULIFLOWER','VEGETABLE','CAULIFLOWER IS ONE OF SEVERAL VEGETABLES IN THE SPECIES BRASSICA OLERACEA IN THE GENUS BRASSICA, WHICH IS IN THE BRASSICACEAE (OR MUSTARD) FAMILY. IT IS AN ANNUAL PLANT THAT REPRODUCES BY SEED.CAULIFLOWER HEADS RESEMBLE THOSE IN BROCCOLI, WHICH DIFFERS IN HAVING FLOWER BUDS AS THE EDIBLE PORTION.CAULIFLOWER IS A CRUCIFEROUS VEGETABLE THAT IS NATURALLY HIGH IN FIBER AND B-VITAMINS.', '2','25','cauliflower.jpg');

--BROCCOLI
INSERT INTO PRODUCT(PRODUCT_ID, PRODUCT_TITLE, CATEGORY, DESCRIPTION, PRICE, QUANTITY, IMAGE) VALUES('9','BROCCOLI','VEGETABLE','BROCCOLI IS AN EDIBLE GREEN PLANT IN THE CABBAGE FAMILY.  IT CAN BE EATEN FRESH OR COOKED. VITAMIN C AND VITAMIN K ARE VERY ABUNDANT IN BROCCOLI. BROCCOLI IS A NUTRIENT-DENSE FOOD THAT CAN HELP YOU LIVE A HEALTHIER LIFE BY REDUCING INFLAMMATION, IMPROVING BLOOD SUGAR CONTROL, INCREASING IMMUNITY, AND ENCOURAGING HEART HEALTH.','1.5','20', 'broccolli.jpg');

