INSERT INTO PRODUCT(PRODUCT_ID, PRODUCT_TITLE, CATEGORY, DESCRIPTION, PRICE, QUANTITY, IMAGE) VALUES('10','EGGPLANT','VEGETABLE','EGGPLANT IS A VEGETABLE THAT COMES IN A VARIETY OF SHAPES, SIZES, AND COLORS AND FLOURISHES IN HOT AREAS AND THROUGHOUT THE SUMMER MONTHS. THEY DIFFER IN SIZE FROM LITTLE GLOBES WITH A DIAMETER OF TWO INCHES TO MORE RECTANGULAR AND TUBULAR VARIETIES WITH A LENGTH OF 12 INCHES OR MORE. ALL EGGPLANT TYPES HAVE EDIBLE SEEDS, MAKING IT PARTICULARLY SIMPLE TO PREPARE.','1','20', 'eggplant.jpg');

--PUMPKIN
INSERT INTO PRODUCT(PRODUCT_ID, PRODUCT_TITLE, CATEGORY, DESCRIPTION, PRICE, QUANTITY, IMAGE) VALUES('11','PUMPKIN','VEGETABLE','A PUMPKIN IS A ROUND WINTER SQUASH CULTIVAR WITH SMOOTH, SLIGHTLY RIBBED SKIN THAT IS USUALLY DEEP YELLOW TO ORANGE IN COLOR. THE SEEDS AND PULP ARE CONTAINED WITHIN THE THICK SHELL. IT IS HIGHLY NUTRITIOUS AND PARTICULARLY RICH IN VITAMIN.', '0.15','90','pumpkin.jpg');

--TOMATO
INSERT INTO PRODUCT(PRODUCT_ID, PRODUCT_TITLE, CATEGORY, DESCRIPTION, PRICE, QUANTITY, IMAGE) VALUES('12','TOMATO','VEGETABLE','TOMATO IS A FLESHY BERRY, USUALLY OF RED COLOUR, ALTHOUGH THERE ARE ALSO PINK OR YELLOW TOMATOES.TOMATOES ARE THE MAJOR DIETARY SOURCE OF THE ANTIOXIDANT LYCOPENE, WHICH HAS BEEN LINKED TO MANY HEALTH BENEFITS, INCLUDING REDUCED RISK OF HEART DISEASE AND CANCER. THEY ARE ALSO A GREAT SOURCE OF VITAMIN C, POTASSIUM, FOLATE, AND VITAMIN K.', '2','25','tomato.jpg');

--CARROT
INSERT INTO PRODUCT(PRODUCT_ID, PRODUCT_TITLE, CATEGORY, DESCRIPTION, PRICE, QUANTITY, IMAGE) VALUES('13','CARROT','VEGETABLE','THE CARROT IS A ROOT VEGETABLE, USUALLY ORANGE IN COLOUR, THOUGH PURPLE, BLACK, RED, WHITE, AND YELLOW CULTIVARS EXIST.THE CARROT IS A BIENNIAL PLANT IN THE UMBELLIFER FAMILY APIACEAE. AT FIRST, IT GROWS A ROSETTE OF LEAVES WHILE BUILDING UP THE ENLARGED TAPROOT.CARROTS ARE A PARTICULARLY GOOD SOURCE OF BETA CAROTENE, FIBER, VITAMIN K1, POTASSIUM, AND ANTIOXIDANTS.','1.5','50','carrot.jpg');



