INSERT INTO PRODUCT(PRODUCT_ID, PRODUCT_TITLE, CATEGORY, DESCRIPTION, PRICE, QUANTITY, IMAGE) VALUES('14','ORANGE','FRUIT','AN ORANGE HAS A TOUGH SHINY ORANGE SKIN. INSIDE, THE FRUIT IS DIVIDED INTO "SEGMENTS", WHICH HAVE THIN TOUGH SKINS THAT HOLD TOGETHER MANY LITTLE SECTIONS WITH JUICE INSIDE. IN SOME ORANGES IT IS HARD TO TAKE THE SKIN OFF. ORANGE SKIN IS OFTEN CALLED "ORANGE PEEL".ORANGES ARE A HEALTHY SOURCE OF FIBER, VITAMIN C, THIAMINE, FOLATE, AND ANTIOXIDANTS. THEY HAVE MULTIPLE HEALTH BENEFITS.','2','20', 'orange.jpg');

--APPLE
INSERT INTO PRODUCT(PRODUCT_ID, PRODUCT_TITLE, CATEGORY, DESCRIPTION, PRICE, QUANTITY, IMAGE) VALUES('15','APPLE','FRUIT','APPLES ARE THE IDEAL FRUIT TO EAT AT ANY TIME, HAVING A POSITIVE ROLE IN THE ACHIEVEMENT OF NOURISH BALANCE. THEIR SKIN MAY BE GREEN, YELLOW OR REDDISH, AND THE MEAT TASTE RANGES FROM A BITTER TO SWEET FLAVOUR.APPLES ARE A POPULAR FRUIT, CONTAINING ANTIOXIDANTS, VITAMINS, DIETARY FIBER, AND A RANGE OF OTHER NUTRIENTS. DUE TO THEIR VARIED NUTRIENT CONTENT, THEY MAY HELP PREVENT SEVERAL HEALTH CONDITIONS.','5','45','apple.jpg');

--PINEAPPLE
INSERT INTO PRODUCT(PRODUCT_ID, PRODUCT_TITLE, CATEGORY, DESCRIPTION, PRICE, QUANTITY, IMAGE) VALUES('16','PINEAPPLE','FRUIT','THE PINEAPPLE IS A TROPICAL FRUITING PLANT THAT IS THE MOST ECONOMICALLY IMPORTANT OF THE BROMELIACEAE FAMILY. THE PINEAPPLE IS NATIVE TO SOUTH AMERICA AND HAS BEEN GROWN THERE FOR GENERATIONS.  IT IS LOW IN CALORIES AND HIGH IN VITAMINS AND MINERALS. ONE CUP OF PINEAPPLE CHUNKS WILL DELIVER: VITAMIN C: YOU WILL GET ONE-THIRD OF YOUR DAILY VALUE OF VITAMIN C WHICH AIDS IN TISSUE GROWTH AND REPAIR.','2','20', 'pineapple.jpg');

--STARWBERRY
INSERT INTO PRODUCT(PRODUCT_ID, PRODUCT_TITLE, CATEGORY, DESCRIPTION, PRICE, QUANTITY, IMAGE) VALUES('17','STARWBERRY','FRUIT','THE STRAWBERRY IS A SUCCULENT AND FRAGRANT FRUIT WITH A BRIGHT RED COLOR THAT COMES FROM THE SAME PLANT. IT IS KNOWN AS THE QUEEN OF THE FRUIT IN THE WEST... THE STRAWBERRY IS A SUCCULENT AND FRAGRANT FRUIT WITH A BRIGHT RED COLOR THAT COMES FROM THE SAME PLANT. THESE POWERFUL LITTLE PACKAGES PROTECT YOUR HEART, INCREASE HDL (GOOD) CHOLESTEROL, REDUCE BLOOD PRESSURE, AND PROTECT YOU FROM CANCER.','2','20','strawberry.jpg');

--MANGO
INSERT INTO PRODUCT(PRODUCT_ID, PRODUCT_TITLE, CATEGORY, DESCRIPTION, PRICE, QUANTITY, IMAGE) VALUES('18','MANGO','FRUIT','MANGOES ARE SWEET, CREAMY FRUITS THAT HAVE A RANGE OF POSSIBLE HEALTH BENEFITS. THEY ARE HIGHLY POPULAR AROUND THE WORLD. THEY ARE A GREAT SOURCE OF MAGNESIUM AND POTASSIUM, BOTH OF WHICH ARE CONNECTED TO LOWER BLOOD PRESSURE AND A REGULAR PULSE. FURTHERMORE, MANGOS ARE THE SOURCE OF A COMPOUND KNOWN AS MANGIFERIN, WHICH EARLY STUDIES SUGGEST MAY BE ABLE TO REDUCE INFLAMMATION OF THE HEART.','2','40', 'mango.jpg');

--BANANA
INSERT INTO PRODUCT(PRODUCT_ID, PRODUCT_TITLE, CATEGORY, DESCRIPTION, PRICE, QUANTITY, IMAGE) VALUES('19','BANANA','FRUIT','A BANANA IS AN ELONGATED, EDIBLE FRUIT – BOTANICALLY A BERRY – PRODUCED BY SEVERAL KINDS OF LARGE HERBACEOUS FLOWERING PLANTS IN THE GENUS MUSA. IN SOME COUNTRIES, BANANAS USED FOR COOKING MAY BE CALLED "PLANTAINS", DISTINGUISHING THEM FROM DESSERT BANANAS.BANANAS ARE A GOOD SOURCE OF POTASSIUM. A DIET HIGH IN POTASSIUM CAN LOWER BLOOD PRESSURE IN PEOPLE WITH ELEVATED LEVELS.','1','40','banana.jpg');

--AVOCADO
INSERT INTO PRODUCT(PRODUCT_ID, PRODUCT_TITLE, CATEGORY, DESCRIPTION, PRICE, QUANTITY, IMAGE) VALUES('20','AVOCADO','FRUIT','THE AVOCADO IS A TREE ORIGINATING IN THE AMERICAS WHICH IS LIKELY NATIVE TO THE HIGHLAND REGIONS OF SOUTH-CENTRAL MEXICO TO GUATEMALA. IT IS CLASSIFIED AS A MEMBER OF THE FLOWERING PLANT FAMILY LAURACEAE. THE FRUIT OF THE PLANT, ALSO CALLED AN AVOCADO, IS BOTANICALLY A LARGE BERRY CONTAINING A SINGLE LARGE SEED.','2.94','40','avocado.jpg');

