INSERT INTO PRODUCT (PRODUCT_ID, PRODUCT_TITLE, CATEGORY, DESCRIPTION, PRICE, QUANTITY, IMAGE) VALUES ('21', 'SALMON', 'FISH','SALMON IS THE COMMON NAME FOR SEVERAL SPECIES OF RAY-FINNED FISH IN THE FAMILY SALMONIDAE. SALMON IS A RICH SOURCE OF OMEGA-3 FATTY ACIDS (EPA AND DHA) AND POTASSIUM. THE OMEGA-3 AND -6 FATTY ACIDS COMBINED WITH POTASSIUM GREATLY CONTRIBUTE TO HEART HEALTH, AS THEY REDUCE ARTERY INFLAMMATION, LOWER CHOLESTEROL LEVELS, AND MAINTAIN BLOOD PRESSURE LEVELS.', '4.5', '35' , 'salmon.jpg');

