INSERT INTO PRODUCT (PRODUCT_ID, PRODUCT_TITLE, CATEGORY, DESCRIPTION, PRICE, QUANTITY, IMAGE) VALUES ('22', 'TUNA', 'FISH','TUNA IS A SALTWATER FISH EATEN ALL OVER THE WORLD. IT IS INCREDIBLY NUTRITIOUS AND A GREAT SOURCE OF PROTEIN, OMEGA-3 FATTY ACIDS.', '3.5', '5' , 'tuna.jpg');

