INSERT INTO PRODUCT (PRODUCT_ID, PRODUCT_TITLE, CATEGORY, DESCRIPTION, PRICE, QUANTITY, IMAGE) VALUES ('23', 'CRAB', 'SHELLFISH','CRABMEAT IS THE MEAT FOUND WITHIN A CRAB. IT IS USED IN MANY CUISINES ACROSS THE WORLD, PRIZED FOR ITS SOFT, DELICATE AND SWEET TASTE.RICH IN VITAMINS AND MINERALS, CRAB IS ALSO LOW IN FAT AND CONTAINS OMEGA-3 POLYUNSATURATED ACIDS. HELPS PROVIDE PROTECTION FROM HEART DISEASE AND AIDS BRAIN DEVELOPMENT.','9', '25' , 'crab.jpg');

--OCTOPUS
INSERT INTO PRODUCT (PRODUCT_ID, PRODUCT_TITLE, CATEGORY, DESCRIPTION, PRICE, QUANTITY, IMAGE) VALUES ('24', 'OCTOPUS', 'SHELLFISH','OCTOPUS IS AN EXCELLENT SOURCE OF OMEGA-3 FATTY ACIDS, "GOOD FATS" LINKED TO A RANGE OF HEART-HEALTHY BENEFITS. OMEGA-3S CAN LOWER YOUR BLOOD PRESSURE AND SLOW THE BUILDUP OF PLAQUE IN YOUR ARTERIES, REDUCING STRESS ON THE HEART.', '10','5', 'octopus.jpg');

--TROUT
INSERT INTO PRODUCT (PRODUCT_ID, PRODUCT_TITLE, CATEGORY, DESCRIPTION, PRICE, QUANTITY, IMAGE) VALUES ('25', 'TROUT', 'FISH','TROUT AND OTHER SALMONIDS CAN EFFICIENTLY DIGEST FOODS THAT CONTAIN PRIMARILY PROTEIN (MOSTLY FROM FISH), AND CAN OBTAIN SOME OF THEIR ENERGY FROM FATS AND, TO A LESSER EXTENT, FROM CARBOHYDRATES. FRY AND FINGERLING TROUT REQUIRE A HIGHER PROTEIN AND ENERGY CONTENT IN THEIR DIETS THAN LARGER FISH.', '3.5', '20' , 'trout.jpg');

--LOBSTER
INSERT INTO PRODUCT (PRODUCT_ID, PRODUCT_TITLE, CATEGORY, DESCRIPTION, PRICE, QUANTITY, IMAGE) VALUES ('26', 'LOBSTER', 'SHELLFISH','THE KING OF THE CRUSTACEANS, LOBSTER IS A DELICACY THAT COMMANDS A VERY HIGH PRICE, WITH WHITE, FIRM MEAT THAT IS SWEET AND SUCCULENT.BEFORE IT IS COOKED, LOBSTER SHELL HAS A VERY DARK COLOUR, WITH TINTS THAT RANGE FROM BLUE/GREEN TO RED/PURPLE, IT GAINS ITS DISTINCTIVE DEEP RED BRICK COLOUR ONLY WHEN IT IS COOKED.LOBSTERS ARE A GREAT SOURCE OF SELENIUM AND ALSO CONTAIN OMEGA-3 FATTY ACIDS. THEY CAN HELP PROTECT AGAINST THYROID DISEASE, DEPRESSION, AND ANEMIA.', '3.46', '15' , 'lobster.jpg');

--SHRIMP
INSERT INTO PRODUCT (PRODUCT_ID, PRODUCT_TITLE, CATEGORY, DESCRIPTION, PRICE, QUANTITY, IMAGE) VALUES ('27', 'SHRIMP', 'SHELLFISH','SHRIMP MAY HAVE A VARIETY OF HEALTH BENEFITS. IT IS HIGH IN SEVERAL VITAMINS AND MINERALS, AND IS A RICH SOURCE OF PROTEIN. EATING SHRIMP MAY ALSO PROMOTE HEART AND BRAIN HEALTH DUE TO ITS CONTENT OF OMEGA-3 FATTY ACIDS AND THE ANTIOXIDANT ASTAXANTHIN.', '5', '25' , 'shrimp.jpg');

--PRAWN
INSERT INTO PRODUCT (PRODUCT_ID, PRODUCT_TITLE, CATEGORY, DESCRIPTION, PRICE, QUANTITY, IMAGE) VALUES ('28', 'PRAWN', 'SHELLFISH','PRAWN ARE IMPORTANT TYPES OF SEAFOOD THAT ARE CONSUMED WORLDWIDE. PRAWNS ARE A RICH SOURCE OF SELENIUM, ONE OF THE MOST EFFECTIVE ANTIOXIDANTS AT MAINTAINING HEALTHY CELLS. THEY ALSO CONTAIN HIGH LEVELS OF ZINC, WHICH IS IMPORTANT TO DEVELOP A HEALTHY IMMUNE SYSTEM. EATING PRAWNS HELPS BUILD STRONG BONES BECAUSE THEY CONTAIN PHOSPHOROUS, COPPER AND MAGNESIUM.','6', '15' , 'prawn.jpg');

--SCAMPI
INSERT INTO PRODUCT (PRODUCT_ID, PRODUCT_TITLE, CATEGORY, DESCRIPTION, PRICE, QUANTITY, IMAGE) VALUES ('29', 'SCAMPI', 'SHELLFISH','THE SHELLED TAIL MEAT IS GENERALLY REFERRED TO AS "SCAMPI TAILS" OR "WHOLETAIL SCAMPI", ALTHOUGH CHEAPER "RE-FORMED SCAMPI" CAN CONTAIN OTHER PARTS TOGETHER WITH OTHER FISH. IT IS SERVED FRIED IN BATTER OR BREADCRUMBS AND USUALLY WITH CHIPS AND TARTARE SAUCE.', '6.5', '10' , 'scampi.jpg');

