--BREAD
INSERT INTO PRODUCT (PRODUCT_ID, PRODUCT_TITLE, CATEGORY, DESCRIPTION, PRICE, QUANTITY, IMAGE) VALUES ('30', 'BREAD','BAKED ITEMS', 'BREAD IS A STAPLE FOOD PREPARED FROM A DOUGH OF FLOUR AND WATER, USUALLY BY BAKING.THE WHOLE GRAINS IN WHOLEMEAL BREAD HAVE MANY BENEFITS. THEY CAN BOOST OVERALL HEALTH AND HELP REDUCE THE RISK OF OBESITY AND VARIOUS OTHER COMPLICATIONS AND DISEASES.', '1.5','50', 'bread.jpg');

--BAGEL
INSERT INTO PRODUCT (PRODUCT_ID, PRODUCT_TITLE, CATEGORY, DESCRIPTION, PRICE, QUANTITY, IMAGE) VALUES ('31', 'BAGEL','BAKED ITEMS', 'A BAGEL (ALSO SPELLED BEIGEL) IS A TYPE OF BREAD, TRADITIONALLY SHAPED BY HAND INTO THE FORM OF A RING FROM WHEAT DOUGH, ROUGHLY HAND-SIZED. ... THE RESULT IS A DENSE, CHEWY, DOUGHY INTERIOR WITH A BROWNED AND SOMETIMES CRISP EXTERIOR, DEPENDING ON BAKE TYPE.BAGELS MADE FROM WHOLE GRAINS MAY HELP BALANCE BLOOD SUGAR, SUPPORT HEALTHY DIGESTION, AND PREVENT DISEASE.', '1','25', 'bagel.jpg');

--MUFFIN
INSERT INTO PRODUCT (PRODUCT_ID, PRODUCT_TITLE, CATEGORY, DESCRIPTION, PRICE, QUANTITY, IMAGE) VALUES ('32', 'MUFFIN','BAKED ITEMS', 'A MUFFIN IS AN INDIVIDUAL-SIZED, BAKED PRODUCT. IT CAN REFER TO TWO DISTINCT ITEMS, A PART-RAISED FLATBREAD THAT IS BAKED AND THEN COOKED ON A GRIDDLE AND A CUPCAKE-LIKE QUICKBREAD THAT IS CHEMICALLY LEAVENED AND THEN BAKED IN A MOLD.', '1','60', 'muffin.jpg');

--CAKE
INSERT INTO PRODUCT (PRODUCT_ID, PRODUCT_TITLE, CATEGORY, DESCRIPTION, PRICE, QUANTITY, IMAGE) VALUES ('33', 'CAKE', 'BAKED ITEMS','CAKE IS A FORM OF SWEET FOOD MADE FROM FLOUR, SUGAR, AND OTHER INGREDIENTS, THAT IS USUALLY BAKED. IN THEIR OLDEST FORMS, CAKES WERE MODIFICATIONS OF BREAD, BUT CAKES NOW COVER A WIDE RANGE OF PREPARATIONS.', '10','20', 'cake.jpg');

--PIZZA
INSERT INTO PRODUCT (PRODUCT_ID, PRODUCT_TITLE, CATEGORY, DESCRIPTION, PRICE, QUANTITY, IMAGE) VALUES ('34', 'PIZZA', 'BAKED ITEMS','PIZZA IS A SAVORY DISH OF ITALIAN ORIGIN CONSISTING OF A USUALLY ROUND, FLATTENED BASE OF LEAVENED WHEAT-BASED DOUGH TOPPED WITH TOMATOES, CHEESE, AND OFTEN VARIOUS OTHER INGREDIENTS WHICH IS THEN BAKED AT A HIGH TEMPERATURE, TRADITIONALLY IN A WOOD-FIRED OVEN.', '4','15', 'pizza.jpg');

--COOKIE
INSERT INTO PRODUCT (PRODUCT_ID, PRODUCT_TITLE, CATEGORY, DESCRIPTION, PRICE, QUANTITY, IMAGE) VALUES ('35', 'COOKIES','BAKED ITEMS', 'COOKIES ARE BROADLY CLASSIFIED ACCORDING TO HOW THEY ARE FORMED OR MADE, INCLUDING AT LEAST THESE CATEGORIES: BAR COOKIES CONSIST OF BATTER OR OTHER INGREDIENTS THAT ARE POURED OR PRESSED INTO A PAN (SOMETIMES IN MULTIPLE LAYERS) AND CUT INTO COOKIE-SIZED PIECES AFTER BAKING. ', '1.5','50', 'cookie.jpg');

--CROISSANT
INSERT INTO PRODUCT (PRODUCT_ID, PRODUCT_TITLE, CATEGORY, DESCRIPTION, PRICE, QUANTITY, IMAGE) VALUES ('36', 'CROISSANT','BAKED ITEMS', 'A CROISSANT IS A BUTTERY, CRESCENT-SHAPED FRENCH PASTRY. GOOD CROISSANTS ARE LIGHT, FLAKY, AND DELICATELY SWEET.COMPARED TO OTHER SWEETS, CROISSANT IS DISTINGUISHED BY THE CONTENT OF PROTEIN, VITAMIN B1 AND VITAMIN B9 (FOLIC ACID).', '3','20', 'croissant.jpg');

--PASTRY
INSERT INTO PRODUCT (PRODUCT_ID, PRODUCT_TITLE, CATEGORY, DESCRIPTION, PRICE, QUANTITY, IMAGE) VALUES ('37', 'PASTRY','BAKED ITEMS', 'PASTRY IS A DOUGH OF FLOUR, WATER AND SHORTENING THAT MAY BE SAVOURY OR SWEETENED. SWEETENED PASTRIES ARE OFTEN DESCRIBED AS BAKERS CONFECTIONERY. THE WORD "PASTRIES" SUGGESTS MANY KINDS OF BAKED PRODUCTS MADE FROM INGREDIENTS SUCH AS FLOUR, SUGAR, MILK, BUTTER, SHORTENING, BAKING POWDER, AND EGGS.', '5','10', 'pastry.jpg');

--Donut
INSERT INTO PRODUCT (PRODUCT_ID, PRODUCT_TITLE, CATEGORY, DESCRIPTION, PRICE, QUANTITY, IMAGE) VALUES ('38', 'DONUT','BAKED ITEMS', 'A doughnut or donut is a type of food made from leavened fried dough. It is popular in many countries and is prepared in various forms as a sweet snack that can be homemade or purchased in bakeries, supermarkets, food stalls, and franchised specialty vendors.', '0.5','50', 'donut.jpg');

